// Type definitions for modules without @types
declare module 'zod' {
  export * from 'zod/lib/types';
  export { default } from 'zod/lib/types';
}

declare module 'date-fns' {
  export * from 'date-fns';
  export { default } from 'date-fns';
}

declare module 'react-toastify' {
  export * from 'react-toastify/dist/types';
  export { default } from 'react-toastify';
}
